import React, { useState } from "react";
import HomePage from "./Components/HomePage";
import LoginPage from "./Components/LoginPage";
import "../src/Style/App.css";
import { BrowserRouter, Route } from "react-router-dom";
import SignInPage from "./Components/SignInPage";
import SignUpPage from "./Components/SignUpPage";
import Profile from "./Components/Profile";
import FilmProfile from "./Components/FilmProfile";
import { Context } from "../src/context";
import LikedFilm from "./Components/LikedFilm";

function App() {
  const [film, setFilm] = useState([]);
  const [liked, setLiked] = useState([]);
  const [movies, setMovies] = useState([]);

  useEffect(()=>{
    dataBase
    .ref(`${userUID}/liked`)
    .on('value', snapshot=>{
      const arr = snapshot.val()
      if(arr){
        arr.length === 0? setLiked([]):setLiked(arr)
      }
    })
  },[])

  return (
    <BrowserRouter>
      <Context.Provider
        value={
          {
            film,
            setFilm,
            liked,
            setLiked,
            movies, 
            setMovies
          }
        }
      >
        <div className="app">
          <Route path="/start" component={LoginPage} />
          <Route path="/signup" component={SignUpPage} />
          <Route path="/login" component={SignInPage} />
          <Route path="/homepage" component={HomePage} />
          <Route path="/profile" component={Profile} />
          <Route path="/filmprofile/:id" component={FilmProfile} />
          <Route path="/liked" component={LikedFilm} />
        </div>
      </Context.Provider>
    </BrowserRouter>
  );
}

export default App;
