import React from "react";

const Sort = ({ options, defaultValue, value, onChange }) => {
  return (
    <select
      className="sortingbtn"
      value={value}
      onChange={(e) => onChange(e.target.value)}
    >
      <option disabled value="">
        {defaultValue}
      </option>
      {options.map((option) => (
        <option value={option.value}>{option.name}</option>
      ))}
    </select>
  );
};

export default Sort;
